import React, { useState } from "react";

export default function App() {
  const [data, setData] = useState(null);
  const [query, setQuery] = useState("");


  const handleSubmit = (e) => {
    e.preventDefault();
    if (!query) return;

    async function fetchData() {
      const BEARER_TOKEN = "3aTio_Vnt6LnEtPLXeighLJhAdc897oPsKJEWyy8wdOdLPYCODBJc8pA2RoR6BAFVfWzVXbW08njQGzGawtyNO0ORkuI09Fou_daCYndCjrQnFBZhD06Fr-OyS7TYXYx"
      const response = await fetch(
        // `https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=Zabiha%20Halal&location=${query}`, {
        `https://api.yelp.com/v3/businesses/search?term=Zabiha%20Halal&location=25724%20Jewel%20Springs%20Ln,%20Katy,%20TX,%2077494`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${BEARER_TOKEN}`,
            mode: "no-cors",
            'Access-Control-Allow-Origin': '*'
          }
        }
      );
      const data = await response.json();
      const results = data.Search;
      setData(results);
    }
    fetchData();
  };

  // console.log(query)
  console.log(data)

  return (
    <div
      style={{
        margin: 20,
      }}
    >
      <form onSubmit={handleSubmit}>
        <br />
        <label>
          Input Address:{" "}
          <input
            type="text"
            placeholder="Address"
            value={query}
            onChange={(e) => {
              setQuery(e.target.value);
            }}
          />
        </label>
        <input type="submit" value="Submit"/>
      </form>

      {/* {data &&
        data.map((movie) => (
