import React, { useEffect } from "react";

// import Header from "./Header";
import SearchBar from "./SearchBar";
// import RestaurantContainer from "./RestaurantContainer";

function App() {

  // const [searchBarContent, setSearchBarContent] = useState("")

  const fetchRestaurantsNearby = (userAddress) => {

    const BEARER_TOKEN = "3aTio_Vnt6LnEtPLXeighLJhAdc897oPsKJEWyy8wdOdLPYCODBJc8pA2RoR6BAFVfWzVXbW08njQGzGawtyNO0ORkuI09Fou_daCYndCjrQnFBZhD06Fr-OyS7TYXYx"
    userAddress.preventDefault()
    useEffect(
      ()=>{
        fetch(`https://api.yelp.com/v3/businesses/search?term=Zabiha%20Halal&location=25724%20Jewel%20Springs%20Ln,%20Katy,%20TX,%2077494`, {
        // fetch(`https://api.yelp.com/v3/businesses/search?term=Zabiha%20Halal&location=${userAddress}`, {
          method: "GET",
          headers: {
            Authorization: `Bearer ${BEARER_TOKEN}`,
            mode: "no-cors",
            'Access-Control-Allow-Origin': '*',
          }
        })
        .then(resp => resp.json())
        .then(data=>console.log(data))
      }, []
    )
  }

  return(
    <div>
      <SearchBar 
        fetchRestaurantsNearby={fetchRestaurantsNearby}
      />
      {/* <Header /> */}
      {/* <RestaurantContainer />  */}
    </div>
  );
}
export default App;


