import React, { useEffect, useState } from "react";

// import Header from "./Header";
import SearchBar from "./SearchBar";
// import RestaurantContainer from "./RestaurantContainer";

function App() {

  const [lat, setLat] = useState(null);
  const [lng, setLng] = useState(null);
  const [status, setStatus] = useState(null);

  const getLocation = () => {
    if (!navigator.geolocation) {
      setStatus('Geolocation is not supported by your browser');
    } else {
      setStatus('Locating...');
      navigator.geolocation.getCurrentPosition((position) => {
        setStatus(null);
        setLat(position.coords.latitude);
        setLng(position.coords.longitude);
      }, () => {
        setStatus('Unable to retrieve your location');
      });
    }
  }
  const BEARER_TOKEN = "3aTio_Vnt6LnEtPLXeighLJhAdc897oPsKJEWyy8wdOdLPYCODBJc8pA2RoR6BAFVfWzVXbW08njQGzGawtyNO0ORkuI09Fou_daCYndCjrQnFBZhD06Fr-OyS7TYXYx"
  useEffect(
    ()=>{
      fetch(`https://cors-anywhere.herokuapp.com/https://api.yelp.com/v3/businesses/search?term=delis&latitude=37.786882&longitude=-122.399972`, {
        method: "GET",
        headers: {
          Authorization: `Bearer ${BEARER_TOKEN}`,
          mode: "no-cors",
          'Access-Control-Allow-Origin': '*',
        }
      })
      .then(resp => resp.json())
      .then(data=>console.log(data))
    }, []
  )
  return (
    <div>
      <SearchBar />
      {/* <Header />
      <RestaurantContainer /> */}
    </div>
  );
}

export default App;
